INSERT INTO `tb_aboutschool` (`id`, `about_id`, `about_menu`, `about_detail`, `about_date`, `about_personnel_id`) VALUES
(1, 'about_001', 'ประวัติโรงเรียน', '<p>&nbsp;&nbsp;&nbsp;<strong>&nbsp;เดิมเป็นโรงเรียนสังกัดกองทัพบก โดยมีมณฑลทหารบกที่ 4 ดูแลและบริหาร ทั้งนี้ เนื่องจากต้องการให้การช่วยเหลือด้านการศึกษาแก่บุตรหลานทหารในค่ายจิรประวัติ โดยทำการสอนที่บ้านพักนายทหารระดับเสนาธิการ ต่อมาได้ขยายกิจการโดยจัดทำเป็นโรงเรียนเปิดทำการสอน ณ บริเวณที่ตั้งวิหารหลวงพ่อขาวในปัจจุบัน (ใกล้บ้านพักผู้บัญชาการมณฑลทหารบกที่ 31 ในปัจจุบัน) โดยใช้ชื่อว่าโรงเรียนทหารสงเคราะห์ เปิดการเรียนการสอนในระดับประถมศึกษา แต่การเรียนการสอนไม่ต่อเนื่อง เนื่องจากอยู่ในภาวะสงครามโลก ครั้งที่ 2</strong></p><p><strong>&nbsp;&nbsp;&nbsp;&nbsp;เมื่อสิ้นสุดสงครามโลก ได้ย้ายกิจการโรงเรียนมาตั้งที่แห่งใหม่เพราะมีนักเรียนเพิ่มมากขึ้น ตามความต้องการของชุมชน โดยใช้ที่ตั้ง ณ แผนกการฝึกกำลังสำรองในปัจจุบัน (ศูนย์การฝึกนักศึกษาวิชาทหาร มณฑลทหารบกที่ 31) ใช้ชื่อโรงเรียนว่า&nbsp;โรงเรียนกองทัพบกอุปถัมภ์&nbsp;ให้อยู่ในความดูแล มณฑลทหารบกที่ 4 โดยให้สวัสดิการมณฑลทหารบกที่ 4 เป็นผู้บริหารและเปลี่ยนชื่อโรงเรียนเป็น&nbsp;โรงเรียนจิรประวัติวิทยาคม&nbsp;ตามชื่อค่ายจิรประวัติ เป็นเกียรติแก่&nbsp;พระเจ้าบรมวงศ์เธอ กรมหลวงนครไชยศรีสุรเดช&nbsp;ซึ่งพระองค์ทรงเป็นต้นราชสกุล \"จิรประวัติ\" เปิดทำการสอนตั้งแต่ชั้นประถมศึกษาจนถึงชั้นมัธยมศึกษาปีที่ 3 เป็นแบบสหศึกษา นักเรียนชายและหญิงใส่เสื้อสีขาว กระโปรงและกางเกงเป็นสีเขียวทหาร ปักอักษรย่อ จ.ป.&nbsp;&nbsp;</strong></p><p><br></p><p class=\"ql-align-center\"><img src=\"https://skj.ac.th/uploads/about/ca2f602f6e930c9d235ca375ec879053a30bb957.jpeg\"></p><p><br></p><p>&nbsp;ปี&nbsp;พ.ศ. 2517&nbsp;พลตรีเยี่ยม อินทรกำแหง ผู้บัญชาการมณฑลทหารบกที่ 4 (ยศและตำแหน่งขณะนั้น) มีความประสงค์จะให้โรงเรียนจิรประวัติวิทยาคม เป็นโรงเรียนที่มีการบริหารจัดการด้านการศึกษาอย่างสมบูรณ์ เพราะมีนักเรียนเพิ่มมากขึ้นจากเดิมมาก จึงได้โอนกิจการโรงเรียนจิรประวัติวิทยาคม จากกองทัพบกไปสังกัดกระทรวงศึกษาธิการ&nbsp;โดยมี นายสายยนต์ เอี่ยมประสงค์ เป็นอาจารย์ใหญ่ ซึ่งยังคงใช้ชื่อโรงเรียนว่า โรงเรียนจิรประวัติวิทยาคม เช่นเดิม แต่เปลี่ยนสีกางเกงและกระโปรงเป็นสีดำ นักเรียนปักอักษรย่อ จ.ว. เปิดทำการสอนตั้งแต่ระดับชั้นประถมศึกษา จนถึงชั้นมัธยมศึกษาปีที่ 3 และให้ลดชั้นประถมศึกษาไปทุกปี จนเหลือเฉพาะระดับชั้นมัธยมศึกษา โดยทำการมอบโรงเรียนอย่างเป็นทางการเมื่อ วันที่ 7 พฤศจิกายน พ.ศ. 2517 นายสำเนาว์ สินาคม เป็นผู้ประสานงาน นายประจวบ คำบุญรัตน์ เป็นตัวแทนของกระทรวงศึกษาธิการ เป็นผู้รับมอบ ต่อมากองทัพบกได้มอบที่ดินให้กับโรงเรียน จำนวน 100 ไร่ โดยใช้เป็นสถานที่ตั้งปัจจุบัน (เดิมคือสนามม้า) ให้เป็นที่ตั้งของโรงเรียนจิรประวัติวิทยาคม ซึ่งเปิดทำการสอนตั้งแต่ชั้นมัธยมศึกษาตอนต้น จนถึงชั้นมัธยมศึกษาตอนปลาย&nbsp;</p><p>พื่อเป็นการสืบสานพระราชปณิธานของ พระบาทสมเด็จพระจุลจอมเกล้าเจ้าอยู่หัว ที่ทรงมีพระราชประสงค์ให้การศึกษาแก่ประชาชนทุกหมู่เหล่าอย่างทั่วถึง โรงเรียนจิรประวัติวิทยาคม ในสมัยของ ดร.กอบกิจ ส่งศิริ จึงขอเข้าเป็น 1 ในเครือ โรงเรียนสวนกุหลาบวิทยาลัย ลำดับที่ 9 โดยใช้ชื่อว่า โรงเรียนสวนกุหลาบวิทยาลัย (จิรประวัติ) นครสวรรค์ ทั้งนี้ได้มองเห็นความเชื่อมต่ออันเป็นรากเหง้าทางการศึกษาอย่างแท้จริง เพราะแม้ พระเจ้าบรมวงศ์เธอ กรมหลวงนครไชยศรีสุรเดช ซึ่งเป็นพระราชโอรสของ พระบาทสมเด็จพระจุลจอมเกล้าเจ้าอยู่หัว กับ เจ้าจอมมารดาทับทิม ก็ทรงเคยศึกษาอยู่ ณ โรงเรียนสวนกุหลาบวิทยาลัย ตามปรากฏหลักฐานเลขประจำพระองค์ 001 และทรงเป็น 1 ใน 18 ศิษย์เก่าดีเด่นสวนกุหลาบวิทยาลัย พระบาทสมเด็จพระปรมินทรมหาภูมิพลอดุลยเดช พระราชทานบรมราชานุญาตประกาศชื่อ โรงเรียนสวนกุหลาบวิทยาลัย (จิรประวัติ) นครสวรรค์ เมื่อวันที่ 27 ธันวาคม พ.ศ. 2550 นับเป็นพระมหากรุณาธิคุณและเกียรติประวัติอย่างยิ่งต่อพสกนิกรชาวนครสวรรค์ อย่างหาที่สุดมิได้</p><p><br></p><p class=\"ql-align-center\"><img src=\"https://skj.ac.th/uploads/about/865b93704de70dab502374b6adb31a5ab30ccfb2.jpg\"></p><p class=\"ql-align-center\">ประวัติ ศาลโกนาศรี</p><p>ศาลโกนาศรี เดิมไม่ปรากฏว่าชื่อใด เรียกกันว่า “ศาลเจ้าที่ประจำโรงเรียน” ตั้งอยู่บริเวณหน้าตึกปิงคงคา (ตึก ๑) โรงเรียนจิรประวัติวิทยาคม ปัจจุบัน คือ โรงเรียนสวนกุหลาบวิทยาลัย (จิรประวัติ) นครสวรรค์ ศาลมีลักษณะเป็นเรือนไม้หลังเล็ก เสาปูน มีการประกอบพิธียกศาลเข้าที่ เมื่อวันที่ ๕ ธันวาคม พ.ศ. ๒๕๒๑ โดยนายสายยนต์ เอี่ยมประสงค์ ผู้อำนวยการโรงเรียน กำหนดให้มีการทำบุญโรงเรียนและพิธีไหว้ศาลเจ้าที่ในวันนี้เป็นประจำทุกปี</p><p>เมื่อ พ.ศ. ๒๕๓๐ มีการบอกเล่าจากความทรงจำของนายกำธร เกิดลาภี ข้าราชการครู โรงเรียนจิรประวัติวิทยาคม ว่าเจ้าที่ได้มาเข้าฝัน ให้สร้างศาลให้ใหม่ ลักษณะศาลเป็นเรือนไทย 3 หลัง และให้ตั้งศาลทางทิศตะวันออกของศาลเดิม โรงเรียนจึงได้ดำเนินการสร้างศาลขึ้นมาใหม่จนแล้วเสร็จ ก่อนจะทำพิธียกศาลเข้าที่ นายเขจร เปรมจิตต์ ผู้อำนวยการโรงเรียนในขณะนั้น ได้สอบถามพระอาจารย์สุพจน์ จันทูปโร เกจิอาจารย์ผู้เรืองเวทย์ เรื่อง การยกศาลเจ้าที่ ซึ่งจำพรรษาอยู่ที่วัดศรีทรงธรรม (วัดบึงปลาทู) อำเภอบรรพตพิสัย จังหวัดนครสวรรค์ ปัจจุบันท่านเป็นเจ้าอาวาส ขณะเมื่อท่านพูดใบหน้ามีอาการสั่น เอ่ยว่า “ครู...ครู...โอม...โอม...หยุดก่อน...หยุดก่อน...เจ้าที่แรง...เจ้าที่แรง...ไม่ใช่เจ้าพ่อเขาเขียว เป็นเจ้าที่นั่น” ท่านจึงตั้งชื่อศาลเจ้าที่ประจำโรงเรียนว่า “ศาลโกนาศรี” ต่อมานายธำรง แพรนิมิต ผู้อำนวยการโรงเรียน ได้เปลี่ยนแปลงกำหนดการประกอบพิธีทำบุญโรงเรียนและพิธีไหว้ศาลโกนาศรี ให้ตรงกับวันสถาปนาโรงเรียนจิรประวัติวิทยาคม ซึ่งตรงกับวันที่ 7 พฤศจิกายนของทุกปีจนถึงปัจจุบัน</p><p class=\"ql-align-center\">ข้อมูลโดย&nbsp;นายกำธร เกิดลาภี ครู หัวหน้ากลุ่มสาระการเรียนรู้คณิตศาสตร์&nbsp;โรงเรียนจิรประวัติวิทยาคม</p><p class=\"ql-align-center\">&nbsp;</p><p class=\"ql-align-center\">เรียบเรียงโดย นางพัชรา วันวงษ์ รองผู้อำนวยการสถานศึกษา</p><p class=\"ql-align-center\">โรงเรียนสวนกุหลาบวิทยาลัย(จิรประวัติ)นครสวรรค์</p><p><br></p>', '2021-07-14 09:12:42', '1'),
(2, 'about_002', ' วิสัยทัศน์-พันธกิจ', '<p><strong style=\"color: rgb(230, 0, 0);\">วิสัยทัศน์</strong></p><p>“โรงเรียนสวนกุหลาบวิทยาลัย (จิรประวัติ) นครสวรรค์ มุ่งพัฒนาผู้เรียนสู่ความเป็นเลิศ ตามมาตรฐานสากลและมาตรฐานกุหลาบหลวง”</p><p><strong>อัตลักษณ์</strong></p><p>“สุภาพบุรุษ-สุภาพสตรีสวนกุหลาบ”</p><p><strong>เอกลักษณ์</strong></p><p>“ภูมิทัศน์ร่มรื่น สดชื่นสวยงาม พร้อมบริการชุมชน”</p><p><strong>พันธกิจ</strong></p><p>1.ส่งเสริมและพัฒนาศักยภาพผู้เรียนทุกคน ให้มีทักษะและคุณลักษณะที่จำเป็นในศตวรรษที่ 21 และสร้างสังคมแห่งการเรียนรู้</p><p>2.ส่งเสริมและพัฒนาประสิทธิภาพระบบการบริหารจัดการการศึกษาเพื่อสร้างความเชื่อมั่นและไว้วางใจ ทุกภาคส่วนของสังคมมีส่วนร่วมในการจัดการศึกษา</p><p>3.ส่งเสริมและพัฒนาผู้เรียนให้มีความเชี่ยวชาญ หนึ่งคน หนึ่งความสามารถตามอัจฉริยะภาพของผู้เรียน สร้างความเป็นผู้นำ รักเพื่อน นับถือพี่ เคารพครู กตัญญูพ่อแม่ ดูแลน้อง สนองคุณแผ่นดิน</p><p>4.ส่งเสริมและพัฒนาสถานศึกษาปรับสู่ความเป็นดิจิทัล ก้าวล้ำเทคโนโลยี เป็นองค์กรที่มีสมรรถนะสูงและทันสมัย</p><p><strong>เป้าประสงค์</strong></p><p>1.นักเรียนมีความเป็นผู้นำตามอัตลักษณ์ของนักเรียนในเครือสวนกุหลาบ</p><p>2.นักเรียน ครูและบุคลากรทางการศึกษา มีคุณธรรม จริยธรรมและค่านิยมที่ถูกต้อง</p><p>3.นักเรียนมีพฤติกรรมการออกกำลังกายอย่างสม่ำเสมอและพัฒนาไปสู่ความเป็นเลิศทางการกีฬา</p><p>นักเรียน ครูและบุคลากรทางการศึกษา มีพฤติกรรมรักการเรียนรู้ตลอดชีวิต และสถานศึกษามีบรรยากาศเป็นสังคมแห่งการเรียนรู้</p><p><br></p>', '2025-08-29 14:58:37', 'pers_021');
