INSERT INTO `tb_news` (`news_id`, `news_topic`, `news_content`, `news_facebook`, `news_date`, `news_img`, `news_category`, `news_view`, `news_vision`, `personnel_id`) VALUES
('news_007', 'เปิดรับสมัครนักเรียนออนไลน์ ระดับชั้นมัธยมศึกษาปีที่ 1 และ 4 ', '<h2><strong>โรงเรียนสวนกุหลาบวิทยาลัย (จิรประวัติ) นครสวรรค์</strong></h2>\r\n<p>เปิดรับสมัครนักเรียนเข้าศึกษาต่อในระดับชั้นมัธยมศึกษาปีที่ 1 และ 4</p>\r\n<p>หลักสูตรที่เปิดรับสมัคร</p>\r\n<ol>\r\n<li>ห้องเรียนความเป็นเลิศทางด้านวิชาการ ( Science Match and Technology Program)</li>\r\n<li>ห้องเรียนความเป็นเลิศทางด้านภาษา ( Chinese English Program)</li>\r\n<li>ห้องเรียนความเป็นเลิศทางด้านดนตรี ศิลปะ การแสดง ( Performing Art Program)</li>\r\n<li>ห้องความเป็นเลิศด้านการงานอาชีพ (Career Program)</li>\r\n</ol>\r\n<h2><strong><span style=\"text-decoration: underline;\">กำหนดการรับสมัคร</span></strong></h2>\r\n<p><strong>เปิดรับสมัคร : </strong>&nbsp;ตั้งแตต่วันที่ 3 - 12 พฤษภาคม 2563</p>\r\n<table style=\"border-collapse: collapse; width: 89.65%; height: 25px; margin-left: auto; margin-right: auto;\" border=\"1\">\r\n<tbody>\r\n<tr style=\"height: 21px;\">\r\n<td style=\"width: 14.8583%; text-align: center; height: 21px;\">\r\n<p>ระดับชั้น</p>\r\n</td>\r\n<td style=\"width: 22.3036%; height: 21px; text-align: center;\">\r\n<p>สอบคัดเลือก</p>\r\n</td>\r\n<td style=\"width: 21.0388%; height: 21px; text-align: center;\">\r\n<p>รายงานตัวและมอบตัว</p>\r\n</td>\r\n</tr>\r\n<tr style=\"height: 22px;\">\r\n<td style=\"width: 14.8583%; text-align: center; height: 22px;\">\r\n<p>ชั้นมัธยมศึกษาปีที่ 1</p>\r\n</td>\r\n<td style=\"width: 22.3036%; height: 22px; text-align: center;\">\r\n<p>30 พฤษภาคม 2563</p>\r\n</td>\r\n<td style=\"width: 21.0388%; height: 22px; text-align: center;\">\r\n<p>6 มิถุนายน 2563</p>\r\n</td>\r\n</tr>\r\n<tr style=\"height: 22px;\">\r\n<td style=\"width: 14.8583%; text-align: center; height: 10px;\">\r\n<p>ชั้นมัธยมศึกษาปีที่ 4</p>\r\n</td>\r\n<td style=\"width: 22.3036%; height: 10px; text-align: center;\">\r\n<p>31 พฤษภาคม 2563</p>\r\n</td>\r\n<td style=\"width: 21.0388%; height: 10px; text-align: center;\">\r\n<p>7 มิถุนายน 2563</p>\r\n</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<p>&nbsp;</p>\r\n<p><strong>สอบถามรายละเอียด</strong>&nbsp;</p>\r\n<p>&nbsp; &nbsp; &nbsp; email : <a href=\"mailto:skjns160@gmail.com\">skjns160@gmail.com</a></p>\r\n<p>&nbsp; &nbsp; &nbsp; โทร : <strong>ฝ่ายธุรการโรงเรียน</strong> 056-255857</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>อาจารย์จริยา เชิดชูพันธ์</strong> 081-3944714</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&nbsp;อาจารย์อรอุมา ฉวีทอง</strong> 084-1789054</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&nbsp;เจ้าหน้าที่วิชาการ นายศุภศิลป์ ศรีนุช</strong> 082-1681654</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>อาจารย์ธีรภัทร เกิดผล&nbsp;</strong> 093-4455773</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<strong>อาจารย์วชิรวิทย์ แกล้วการไถ</strong> 091-0518473</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <strong>&nbsp;อาจารย์วชระ พรหมพันธกรณ์</strong> 094-0070105</p>\r\n<p><img style=\"display: block; margin-left: auto; margin-right: auto;\" title=\"การรับสมัครนักเรียน.png\" src=\"data:image/png;base64,\" alt=\"\" width=\"308\" height=\"308\" /></p>\r\n<p style=\"text-align: center;\">ลิงก์การรับสมัครทางออนไลน์ : <a href=\"../../../RegStudent\">skj.ac.th/RegStudent</a></p>\r\n<p style=\"text-align: center;\">ตรวจสอบข้อมูลการสมัครเรียน : <a href=\"../../../checkRegister\">skj.ac.th/checkRegister</a></p>\r\n<p style=\"text-align: center;\">&nbsp;</p>', '', '2020-04-16 10:35:16', '19dd1554b3882ddfe401c217ff945930.jpg', 'ข่าวประชาสัมพันธ์', 1814, 0, 'pers_001'),
('news_008', 'ตรวจสอบและแก้ไขข้อมูลการสมัครเรียน (รอบสุดท้าย) ปีการศึกษา 2563', '<h3>เปิดให้ตรวจสอบและแก้ไขข้อมูลตั้งแต่วันนี้จนถึงวันที่ 20 พฤษภาคม พ.ศ. 2563</h3>\r\n<h3>ทำการแก้ไขให้ถูกต้อง&nbsp; เพื่อพิมพ์ใบสมัครสอบ ไว้ใช้ยืนยันตัวเองให้การเข้าสอบคัดเลือก</h3>\r\n<p>&nbsp;</p>\r\n<h3>คลิกตรวจสอบที่ลิงก์ : <a href=\"../../Announce?a=4\">skj.ac.th/Announce?a=4</a>&nbsp; หรือ&nbsp; คลิกที่เมนู \"รับสมัครนักเรียน\"</h3>\r\n<h3>&nbsp;</h3>\r\n<p>&nbsp;</p>', '', '2020-05-15 16:44:32', 'bb3f8c9079cbe4391d27517f1f3540f0.jpg', 'ข่าวประชาสัมพันธ์', 1839, 0, '1'),
('news_010', 'ช่องทางการเรียนทางไกลผ่าน DLTV มูลนิธิการศึกษาทางไกลผ่านดาวเทียมในพระบรมราชูปถัมภ์', '<p>&nbsp;</p>\r\n<h3 style=\"text-align: center;\">มูลนิธิ การ ศึกษา ทาง ไกล ผ่าน ดาวเทียม ใน พระบรม ราชูปถัมภ์ - DLTV</h3>\r\n<h3 style=\"text-align: center;\">เว็บไซต์หลัก <a href=\"https://www.dltv.ac.th/channels/tv\">https://www.dltv.ac.th/channels/tv</a></h3>\r\n<p>&nbsp;</p>\r\n<h3 style=\"text-align: center;\">ตารางออกอากาศ <a href=\"https://www.dltv.ac.th/schedules\">https://www.dltv.ac.th/schedules</a></h3>', '', '2020-05-18 15:22:18', '427db948792443ecfcbb0fee2ec9e1e1.jpg', 'ข่าวประชาสัมพันธ์', 1437, 0, 'pers_022');
